  <div class="card" id="cardfour">
    <h2 class="text">Title: Lolita</h2>
    <h3 class="text">Price: $9</h3>
    <img
      class="image"
      src="data:image/jpeg;base64,"
      width="150"
      height="150"
    />
    <div></div>
    <button @click="togglefour" id="buttontwo" class="button">purchase</button>
